package cn.scholarprofile.dto;/** *  * @author pangchao E-mail: pangchao620@163.com * @date : 2015年12月7日 下午4:25:06 * @Description : UserInfo bean * @version 1.0 */public class UserInfoRegistStep01{		/** 真实姓名 **/	private String realName;	/** 出生日期 **/	private String birthday;	/** 性别 **/	private String sex;	/** 最高学历 **/	private String maxDegree;	/** 工作年限 **/	private String worklife;	/** 工作地点 **/	private String workPlace;	/**	 * @return the realName	 */	public String getRealName() {		return realName;	}	/**	 * @param realName	 *            the realName to set	 */	public void setRealName(String realName) {		this.realName = realName;	}		public String getBirthday() {		return birthday;	}	public void setBirthday(String birthday) {		this.birthday = birthday;	}	/**	 * @return the sex	 */	public String getSex() {		return sex;	}	/**	 * @param sex	 *            the sex to set	 */	public void setSex(String sex) {		this.sex = sex;	}	/**	 * @return the maxDegree	 */	public String getMaxDegree() {		return maxDegree;	}	/**	 * @param maxDegree	 *            the maxDegree to set	 */	public void setMaxDegree(String maxDegree) {		this.maxDegree = maxDegree;	}		public String getWorklife() {		return worklife;	}	public void setWorklife(String worklife) {		this.worklife = worklife;	}	/**	 * @return the workPlace	 */	public String getWorkPlace() {		return workPlace;	}	/**	 * @param workPlace	 *            the workPlace to set	 */	public void setWorkPlace(String workPlace) {		this.workPlace = workPlace;	}}