package cn.scholarprofile.dto;/** *  * @author pangchao E-mail: pangchao620@163.com * @date : 2015年12月7日 下午4:25:06 * @Description : UserInfo bean * @version 1.0 */public class UserInfoRegistStep02{		/** 当前行业与职能 **/	private String industryAndFunctional;	/** 公司名 **/	private String companyName;	/** 职位 **/	private String curPosition;	/** 电话号码 **/	private String phone;	/** 感兴趣的领域 **/	private String interestField;	/**	 * @return the industryAndFunctional	 */	public String getIndustryAndFunctional() {		return industryAndFunctional;	}	/**	 * @param industryAndFunctional	 *            the industryAndFunctional to set	 */	public void setIndustryAndFunctional(String industryAndFunctional) {		this.industryAndFunctional = industryAndFunctional;	}	/**	 * @return the companyName	 */	public String getCompanyName() {		return companyName;	}	/**	 * @param companyName	 *            the companyName to set	 */	public void setCompanyName(String companyName) {		this.companyName = companyName;	}	/**	 * @return the curPosition	 */	public String getCurPosition() {		return curPosition;	}	/**	 * @param curPosition	 *            the curPosition to set	 */	public void setCurPosition(String curPosition) {		this.curPosition = curPosition;	}	/**	 * @return the phone	 */	public String getPhone() {		return phone;	}	/**	 * @param phone	 *            the phone to set	 */	public void setPhone(String phone) {		this.phone = phone;	}	/**	 * @return the interestField	 */	public String getInterestField() {		return interestField;	}	/**	 * @param interestField	 *            the interestField to set	 */	public void setInterestField(String interestField) {		this.interestField = interestField;	}}